mapper.select:
SELECT COUNT(*)
FROM TBL_RESERVATION
WHERE MACHINE_NO = #{machineNo}
AND RESERVE_DATE = #{reserveDate}
AND (START_TIME < #{endTime} AND END_TIME > #{startTime})

SELECT MACHINE_STATUS
FROM TBL_MACHINE
WHERE MACHINE_NO = #{machineNo}

mapper.update:
UPDATE TBL_RESERVATION 
SET QR_CODE = #{qrCode}
WHERE RESERVE_NO = #{reserveNo}
